// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Context.sol";

contract SIRCMS is Ownable {

    // --- Structs ---
    struct Institution {
        address institutionAddress;
        string name;
        uint224 code;       // <--- CHANGED TO uint224
        bool isRegistered;
    }

    struct Credential {
        uint256 studentId;
        uint256 schoolId;
        string studentName;
        uint256 dateOfBirth;
        string institutionName;
        string certificateTitle;
        uint256 issueDate;
        uint256 expiryDate;
        string documentHash;
        string ipfsCid;
        bool isRevoked;
    }

    // --- State Variables ---
    mapping(address => Institution) public institutionsData;
    mapping(uint224 => bool) public institutionCodeExists;
    address[] public registeredInstitutionAddresses;
    mapping(bytes32 => Credential) public credentials;

    // --- Events ---
    event InstitutionRegistered(address indexed institutionAddress, string name, uint256 code);
    event CredentialStored(
        uint256 indexed studentId,
        uint256 indexed schoolId,
        string studentName,
        string institutionName,
        string certificateTitle,
        string documentHash
    );
    event CredentialRevoked(uint256 indexed studentId, uint256 indexed schoolId);

    // --- Constructor ---
    constructor(address initialOwner) Ownable(initialOwner) {}

    // --- Modifiers ---
    modifier onlyRegisteredInstitutionOrOwner() {
        require(
            institutionsData[msg.sender].isRegistered || msg.sender == owner(),
            "Caller is not a registered institution or the contract owner"
        );
        _;
    }

    // --- Internal Helper Functions ---
    function _getCredentialKey(uint256 _studentId, uint256 _schoolId) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(_studentId, _schoolId));
    }

    // --- External/Public Functions ---

    // Function to register a new institution (Owner only)
    function registerInstitution(address _institutionAddress, string memory _name, uint224 _code) // <--- CHANGED TO uint224
        public
        onlyOwner
    {
        require(_institutionAddress != address(0), "Invalid institution address");
        require(!institutionsData[_institutionAddress].isRegistered, "Institution already registered");
        require(!institutionCodeExists[_code], "Institution code already exists");
        require(_code >= 100000000 && _code <= 999999999, "Institution code must be a 9-digit number");

        institutionsData[_institutionAddress] = Institution({
            institutionAddress: _institutionAddress,
            name: _name,
            code: _code, // This will now match the type
            isRegistered: true
        });
        institutionCodeExists[_code] = true;
        registeredInstitutionAddresses.push(_institutionAddress);

        emit InstitutionRegistered(_institutionAddress, _name, _code);
    }

    // Function to get all registered institution addresses (public view)
    function getAllRegisteredInstitutionAddresses() public view returns (address[] memory) {
        return registeredInstitutionAddresses;
    }

    // Function to store a new credential
    function storeCredential(
        uint256 _studentId,
        uint256 _schoolId,
        string memory _studentName,
        uint256 _dateOfBirth,
        string memory _institutionName,
        string memory _certificateTitle,
        uint256 _issueDate,
        uint256 _expiryDate,
        string memory _documentHash,
        string memory _ipfsCid
    ) public onlyRegisteredInstitutionOrOwner {
        bytes32 key = _getCredentialKey(_studentId, _schoolId);
        require(credentials[key].studentId == 0, "Credential already exists");

        require(bytes(_documentHash).length > 0, "Document hash cannot be empty");
        require(bytes(_ipfsCid).length > 0, "IPFS CID cannot be empty");
        require(_dateOfBirth > 0, "Invalid date of birth");
        require(_issueDate > 0, "Invalid issue date");
        if (_expiryDate != 0) {
            require(_expiryDate >= _issueDate, "Expiry date cannot be before issue date");
        }
        if (msg.sender != owner()) {
            require(keccak256(abi.encodePacked(institutionsData[msg.sender].name)) == keccak256(abi.encodePacked(_institutionName)),
                            "Institution name mismatch for registered issuer");
        }

        credentials[key] = Credential({
            studentId: _studentId,
            schoolId: _schoolId,
            studentName: _studentName,
            dateOfBirth: _dateOfBirth,
            institutionName: _institutionName,
            certificateTitle: _certificateTitle,
            issueDate: _issueDate,
            expiryDate: _expiryDate,
            documentHash: _documentHash,
            ipfsCid: _ipfsCid,
            isRevoked: false
        });

        emit CredentialStored(_studentId, _schoolId, _studentName, _institutionName, _certificateTitle, _documentHash);
    }

    // Function to search for a credential
    function searchCredential(uint256 _studentId, uint256 _schoolId)
        public
        view
        returns (
            uint256 studentId,
            uint256 schoolId,
            string memory studentName,
            uint256 dateOfBirth,
            string memory institutionName,
            string memory certificateTitle,
            uint256 issueDate,
            uint256 expiryDate,
            string memory documentHash,
            string memory ipfsCid,
            bool isRevoked
        )
    {
        bytes32 key = _getCredentialKey(_studentId, _schoolId);
        Credential storage cred = credentials[key];
        require(cred.studentId != 0, "Credential not found");

        return (
            cred.studentId,
            cred.schoolId,
            cred.studentName,
            cred.dateOfBirth,
            cred.institutionName,
            cred.certificateTitle,
            cred.issueDate,
            cred.expiryDate,
            cred.documentHash,
            cred.ipfsCid,
            cred.isRevoked
        );
    }

    // Function to revoke a credential (only by issuer or owner)
    function revokeCredential(uint256 _studentId, uint256 _schoolId) public onlyRegisteredInstitutionOrOwner {
        bytes32 key = _getCredentialKey(_studentId, _schoolId);
        Credential storage cred = credentials[key];
        require(cred.studentId != 0, "Credential not found");
        require(!cred.isRevoked, "Credential is already revoked");

        if (msg.sender != owner()) {
            require(
                keccak256(abi.encodePacked(institutionsData[msg.sender].name)) == keccak256(abi.encodePacked(cred.institutionName)),
                "Only the issuing institution or contract owner can revoke this credential"
            );
        }

        cred.isRevoked = true;
        emit CredentialRevoked(_studentId, _schoolId);
    }

    // Function to verify a credential's validity
    function verifyCredential(uint256 _studentId, uint256 _schoolId, string memory _documentHash)
        public
        view
        returns (bool isValid, bool isRevoked, bool isExpired)
    {
        bytes32 key = _getCredentialKey(_studentId, _schoolId);
        Credential storage cred = credentials[key];

        if (cred.studentId == 0) {
            return (false, false, false);
        }

        isRevoked = cred.isRevoked;
        isExpired = false;

        if (cred.expiryDate != 0 && block.timestamp > cred.expiryDate) {
            isExpired = true;
        }

        isValid = (!isRevoked && !isExpired && keccak256(abi.encodePacked(cred.documentHash)) == keccak256(abi.encodePacked(_documentHash)));

        return (isValid, isRevoked, isExpired);
    }
}
